INSERT INTO `wp_options` VALUES("3057", "give_settings", "a:16:{i:0;b:0;s:12:\"success_page\";i:1001;s:12:\"failure_page\";i:1002;s:12:\"history_page\";i:1003;s:12:\"base_country\";s:2:\"US\";s:9:\"test_mode\";s:2:\"on\";s:8:\"currency\";s:3:\"USD\";s:16:\"session_lifetime\";s:6:\"604800\";s:8:\"gateways\";a:2:{s:6:\"manual\";s:1:\"1\";s:7:\"offline\";s:1:\"1\";}s:15:\"default_gateway\";s:6:\"manual\";s:31:\"global_offline_donation_content\";s:411:\"In order to make an offline donation we ask that you please follow these instructions:\r\n<ol>\r\n	<li>Make a check payable to \"Blessing The Children\"</li>\r\n	<li>On the memo line of the check, please indicate that the donation is for the child or program you would like to sponsor</li>\r\n	<li>Please mail your check to:</li>\r\n</ol>\r\n&nbsp;\r\n\r\nAll contributions will be gratefully acknowledged and are tax deductible.\";s:21:\"donation_notification\";s:401:\"Hi there,\n\nThis email is to inform you that a new donation has been made on your website: <a href=\"http://griffin.technicalbd.com/customer/griffin\" target=\"_blank\">http://griffin.technicalbd.com/customer/griffin</a>.\n\n<strong>Donor: </strong>  {name}\n<strong>Donation: </strong>  {donation}\n<strong>Amount: </strong>  {price}\n<strong>Payment Method: </strong>  {payment_method}\n\nThank you,\n\n{sitename}\";s:12:\"paypal_email\";s:31:\"kstrawn@blessingthechildren.org\";s:18:\"paypal_button_type\";s:8:\"donation\";s:24:\"offline_donation_subject\";s:42:\"{donation} - Offline Donation Instructions\";s:29:\"global_offline_donation_email\";s:649:\"Dear {name},\r\n\r\nThank you for your offline donation request! Your generosity is greatly appreciated. In order to make an offline donation we ask that you please follow these instructions:\r\n<ol>\r\n	<li>Make a check payable to \"Griffin\"</li>\r\n	<li>On the memo line of the check, please indicate that the donation is for \"Griffin\"</li>\r\n	<li>Please mail your check to:</li>\r\n</ol>\r\n<em>Griffin</em>\